INSERT INTO `ui_singlehtml` (`id`, `typeid`, `title`, `keywords`, `description`, `userid`, `ctime`, `uptime`, `views`, `orderid`, `html`) VALUES
(1, 103, '关于我们', 'help', '关于页面', 0, 1460022961, 1464664172, 0, 0, '<p class="alert alert-success">\r\n	欢迎加入100days ui的旅程，在这里每天我们会更新一个UI任务（周一到周五更新）。你只需要根据选题发挥你的创造力，完成设计，然后点击上传作品提交，即可完成今日任务。你的设计将会展示给同行的小伙伴们， 勉励他们继续前进。坚持100天，望你破茧成蝶！\r\n</p>\r\n<p>\r\n	<h3>\r\n		v1.3版本更新\r\n	</h3>\r\n新增广场页面，快速查看优秀作品，互相学习；查看设计师排行榜，看看自己的进度。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<h3>\r\n	v1.2版本更新\r\n</h3>\r\n1.新增排行榜功能<br />\r\n2.新增会员删除评论功能<br />\r\n3.优化检索内容<br />\r\n4.修改细节bug<br />\r\n5.总之就是做了很多的体验提升，不信你自己试试<br />\r\n<h3>\r\n	v1.1版本更新\r\n</h3>\r\n1.每日作业能够看作品了！在”作品展示“可以看到关于这一天题目的所有作品<br />\r\n2.每日作业更新由原来一天一次更改为每周五次（周一到周五）<br />\r\n3.设计师列表点击”更多“，可以看到所有设计师了<br />\r\n4.作品已加上收藏与点赞<br />\r\n5.设计师作品页进行了重新排版，设计师也可以互相关注了<br />\r\n6.「VIP会员」给你带来更优质的服务\r\n<h3>\r\n	v1.0版本\r\n</h3>\r\nUI100day正式上线，一个设计师磨炼设计内功，交流设计心得的世界。每天一个UI任务，100天的坚持回馈你的将是走向大神的梦想。 愿这个梦想成为现实的时候，你还能回忆起自己当年冒险的旅程。<br />\r\n【每日UI】每日UI任务，根据选题发挥创造力，完成设计。<br />\r\n【往期回顾】完成的UI作品回顾，每一天开启一个作品，完成后作品会显示为彩色。<br />\r\n【上传作品】快速上传你做好的作品，选择作品天数与标题描述即可上传。<br />');
INSERT INTO `ui_site` (`id`, `name`, `domain`, `keywords`, `description`, `title`, `stage`) VALUES
(1, 'UI100day,UI设计师学习习惯培养社区', 'http://www.ui100day.com', 'ui,ui100day,UI100day', 'ui100day，UI设计师学习习惯培养，免费UI设计学习,UI设计点评，UI设计在线学习', 'ui100day,UI设计师学习习惯养成,UI作品点评', 1);
INSERT INTO `ui_type` (`id`, `pid`, `title`, `model`, `content`, `urlname`, `keywords`, `description`, `thumb`, `orderid`, `template_list`, `template_content`) VALUES
(1, 0, '第一期', 'work', '', '', '', '资源管理器界面<br/>\r\n电脑或者手机文件的管理器界面，大小尺寸不限，颜样式不限制，需要包含文件夹，导航等信息<br/>\r\n参考图作者：Mik Skuza<br/>', '', 0, 'list', 'content'),
(2, 1, '登陆页(Sign In)', 'work', '', '', '', '欢迎来到UI100day！第一天，请给UI100day做一个登陆页，开启你的100天之旅！\r\n<div style="font-size:16px;color:#fff;">包含信息<br>\r\n1.100day欢迎信息\r\n2.登陆标识（按钮、箭头等）\r\n</div>', 'http://img.ui100day.com/2016-06-06_57556326bdb57.png', 1, '', ''),
(3, 1, '聊天界面(IM - Chat)', 'work', '', '', '', '不用说，每天都要用，那么就做一个自己喜欢的聊天界面吧。包括用户列表界面和聊天窗口界面<br>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.头像   \r\n2.用户名   \r\n3.聊天内容   \r\n4.发送时间   \r\n5.导航   \r\n6.输入框   \r\n</div>', 'http://img.ui100day.com/old56923617804e0.jpg', 2, '', ''),
(4, 1, '工作规划（to do list）', 'work', '', '', '', '对于每天都要处理大量事务的同学，一款交互效率高又好看的任务规划应用简直就是工作利器！<br>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.任务列表\r\n2.任务名称\r\n3.任务详情\r\n4.任务功能按键\r\n5.导航  \r\n</div>', 'http://img.ui100day.com/old56949d3a6fd65.jpg', 3, '', ''),
(5, 1, '地图(Map)', 'work', '', '', '', '出行必备，常用那几个就不说了，能不能再酷些。如果没有灵感，可以参考一下极品飞车的地图。<br>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.地图主题\r\n2.定位信息\r\n3.地点选择\r\n4.搜索\r\n5.导航  \r\n</div>', 'http://img.ui100day.com/2016-06-06_575564e6d811c.jpg', 4, '', ''),
(6, 1, '日历应用(Calendar)', 'work', '', '', '', '一款赏心悦目的日历应用，总是让人想多打开几次。<br/>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.日期\r\n2.添加事件按钮\r\n3.事件标题\r\n4.事件详情\r\n</div>', 'http://img.ui100day.com/old569615b3cd91c.jpg', 5, '', ''),
(7, 1, '天气界面(weather)', 'work', '', '', '', '出门前看看，旅行前看看，需要穿什么就有谱了。<br/>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.天气状况\r\n2.温度\r\n3.日期\r\n</div>', 'http://img.ui100day.com/old5696186bc7409.jpg', 6, '', ''),
(8, 1, '快递进度查询(Delivery check)', 'work', '', '', '', '等了这么久还没到，我要催一下快递小哥！（快递查询或者外卖查询均可）<br>\r\n<div style="font-size:16px;color:#fff;">包含信息<br>\r\n1.商品信息\r\n2.送货进度\r\n3.时间信息\r\n</div>', 'http://img.ui100day.com/old56961b6c285ef.jpg', 7, '', ''),
(9, 1, '音乐播放界面(music player)', 'work', '', '', '', 'Come some abula music！（PC端手机端均可）<br/>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.播放界面（播放按钮，进度条，歌曲信息）\r\n2.歌曲列表\r\n3.导航\r\n</div>', 'http://img.ui100day.com/old56961bbeb435e.jpg', 8, '', ''),
(10, 1, '视频播放器(video player)', 'work', '', '', '', '要高清，要..（桌面端或者手机端均可）<br/>\r\n<div style="font-size:16px;color:#fff;">包含信息<br>\r\n1.视频信息\r\n2.播放按钮\r\n3.播放进度条  \r\n</div>', 'http://img.ui100day.com/2016-06-07_57566f752214d.jpg', 9, '', ''),
(11, 1, '注册界面(Sign Up)', 'work', '', '', '', '优美好用的注册表单，本身就可以成为注册的理由（注册、登录页面均可）<br>\r\n<div style="font-size:16px;color:#fff;">包含信息<br>\r\n1.用户名、密码等必要信息\r\n2.登录、注册按钮\r\n</div>', 'http://img.ui100day.com/2016-06-06_575540d78df35.png', 10, '', ''),
(12, 1, '电影管理应用界面（Movie Application）', 'work', '', '', '', '跟电影相关列表类界面重设计（PC端手机端自拟）<br/>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.电影海报\r\n2.电影信息（名称、参与人员、简介）\r\n3.评分\r\n4.搜索\r\n</div>', 'http://img.ui100day.com/old569dc35d3ffa5.jpg', 11, '', ''),
(13, 1, '个人运动数据界面（Sport Tracking App）', 'work', '', '', '', '跟运动数据相关的个人中心设计<br/>\r\n<div style="font-size:16px;color:#fff;">作品需要包含的信息<br>\r\n1.个人头像与昵称\r\n2.运动数据\r\n3.图表\r\n</div>', 'http://img.ui100day.com/old569dc42125040.jpg', 12, '', ''),
(14, 1, '飞机票界面（Boarding pass）', 'work', NULL, NULL, '', '做个移动端票类的设计，飞机票或者是火车票<br/>\r\n参考图作者：Aurélien Salomon ➔', 'http://img.ui100day.com/old569dc45c01cb5.jpg', 13, NULL, NULL),
(15, 1, '阅读书籍界面（Library App）', 'work', NULL, NULL, '', '推荐书籍、阅读类的UI界面设计<br/>\r\n参考图作者：Nicholas', 'http://img.ui100day.com/old569dc48747d28.jpg', 14, NULL, NULL),
(16, 1, '图书有声阅读界面（Books app Concept）', 'work', NULL, NULL, '', '读书购书app界面，包括选书和读书两个页面<br></br>参考图作者：Gleb Kuznetsov', 'http://img.ui100day.com/old56a0ba925cd9f.jpg', 15, NULL, NULL),
